snippet.text:
x="`date '+%m'`"
y="`date '+%Y'`"
z="`date '+%d'`"

z="`expr ${z} + 1`"
cal ${x} ${y} | grep -w ${z} > /dev/null || {
        z="01"
        x="`expr ${x} + 1`"
        [ "${x}" -lt "10" ] && x="0${x}"
        [ "${x}" = "13" ] &+ 1`"
        }
}

[ "${1}" = "MMDDYYYY" -o "${1}" = "mmddyyyy" -o -z "${1}" ] &${z}${y}"
        echo ${tomorrow}
        exit 0
}
[ "${1}" = "DDMMYYYY" -o "${1}" = "ddmmyyyy" ] &${x}${y}"
} || {
        echo "Invalid format! Using the default:"
        tomorrow="${x}${z}${y}"
}
echo ${tomorrow}
